    private void yesButtonActionPerformed(java.awt.event.ActionEvent evt) {                                          
        AllergiePage2 p2 = new AllergiePage2();
        p2.setSize(700, 270);
        p2.setLocation(0,0);
        panelChoix.removeAll();
        panelChoix.add(p2, BorderLayout.CENTER);
        panelChoix.add(p2); 
        panelChoix.revalidate();
        panelChoix.repaint(); 
        validerButton.hide();
      
    }                                         

